(function() {

	var A, B, C;

	A = screen.width;

	if (A < 481)
		B = 'xsmall';
	else if (A < 737)
		B = 'small';
	else if (A < 981)
		B = 'medium';
	else if (A < 1141)
		B = 'large';
	else
		B = 'xlarge';

	C = {
		state: B,
		track: function(fn) { if (typeof fn == 'function') fn(B); }
	};

	if (typeof window.breakpoints != 'undefined')
		window.breakpoints = C;

})();

