(function() {

	var B = {
		name: navigator.userAgent.toLowerCase(),
		jquery: false
	};

	B.ielt9 = /msie 9/i.test(B.name);
	B.ielt10 = /msie 10/i.test(B.name);
	B.ielt11 = /msie 11/i.test(B.name);

	if (typeof jQuery != 'undefined')
		B.jquery = true;

	if (B.ielt9)
		document.documentElement.className += ' ielt9';
	if (B.ielt10)
		document.documentElement.className += ' ielt10';
	if (B.ielt11)
		document.documentElement.className += ' ielt11';

	if (typeof window.browser != 'undefined')
		window.browser = B;

})();

